RID = T0.UserSign LEFT JOIN OUSR T8 ON T8.USERID = T0.UserSign2 WHERE T0.PostDate >= '20230401'     AND T0.Status in ('L','R') AND  (ISNULL(A.value,0) + ISNULL(B.value,0) + ISNULL(c.value,0)) <> 0  AND T0.Type ='S'
